<style lang="scss">
  .illustration-wrapper {
    --stroke-wrapper-bg-clr: #fff;

    width: clamp(300px, 100%, 500px);
    margin: 0 auto;
    padding: 0 5%;
    display: grid;
    grid-template-columns: 1fr;
    grid-template-rows: 1fr;
    align-items: center;
    justify-items: center;

    &__background {
      width: 90%;
      grid-column: 1 / -1;
      grid-row: 1 / 2;
      z-index: 0;
    }

    &__icon-sets {
      width: 70%;
      grid-column: 1 / -1;
      grid-row: 1 / 2;
      z-index: 1;
    }
  }

  :global(.app-wrapper[dark-mode="true"]) .illustration-wrapper {
    --stroke-wrapper-bg-clr: var(--code-bg-clr-secondary);
  }

  @media screen and (min-width: 768px) {
    .illustration-wrapper {
      padding: 0;
    }
  }

  @media (min-width: 1100px) {
    .illustration-wrapper {
      width: 600px;
    }
  }
</style>
